import React from 'react';
import ProfilePicture from '../avatar.jpg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLinkedin, faTwitter, faGithub, faCodepen } from '@fortawesome/free-brands-svg-icons'; 

const Avatar = () =>
    <div className="img">
        <img className="img-photo" src={ProfilePicture} alt="Steven McStravog" />
    </div>;

const Description = () =>
    <div>
        <h1>Steven McStravog</h1>
        <p>I am a enthusiastic software engineer with several years industrial experience.</p>
    </div>;

const Follow = () =>
    <React.Fragment>
        <h6>FOLLOW ME ON</h6>
        <FontAwesomeIcon icon={faLinkedin} />
        <FontAwesomeIcon icon={faTwitter} />
        <FontAwesomeIcon icon={faGithub} />
        <FontAwesomeIcon icon={faCodepen} />
    </React.Fragment>;

const Contact = () =>
    <React.Fragment>
        <h6>GET IN TOUCH</h6>
    </React.Fragment>;

const About = () =>
    <div className="text-right mr-2 mb-5">
        <Avatar />
        <Description />
        <Follow />
        <Contact />
    </div>;

export default About;
